mapper.select:
SELECT COUNT(*) FROM faq
WHERE TYPE1 = #{type1}
AND TYPE2 = #{type2}

SELECT *
FROM FAQ
WHERE TYPE1 = #{type1}
AND TYPE2 = #{type2}
ORDER BY ID DESC
FETCH FIRST 10 ROWS ONLY

mapper.update:
UPDATE FAQ SET
TYPE1 = #{type1}, TYPE2 = #{type2}, TITLE = #{title}, CONTENT = #{content}
WHERE ID = #{id}

UPDATE FAQ SET
VIEWS = #{views}
WHERE ID = #{id}

