mapper.insert:

    SELECT 'ORD' || LPAD(ORDER_SEQ.NEXTVAL, 8, '0') FROM DUAL


INSERT INTO "ORDER" (
ORDER_NUMBER, CUST_NUMBER, O_DATE, STAT,
NAME, HP, ADDR, ADDR2, PAYMENT, PRICE, USE_POINT, COUPON_DISCOUNT
) VALUES (
#{order_number}, #{cust_number}, SYSDATE, 1,
#{name}, #{hp}, #{addr}, #{addr2}, #{payment},
#{price}, #{usePoint}, #{couponDiscount}
)

INSERT INTO ORDERDETAIL (
    ORDER_NUMBER, PROD_NUMBER, PRICE, PIECE
) VALUES (
             #{order_number}, #{prod_number}, #{salePrice}, #{piece}
         )

mapper.select:
SELECT * FROM CART
WHERE CART_NUMBER IN

    #{num}

SELECT NVL(SUM(POINT_AMOUNT), 0)
FROM POINT
WHERE CUST_NUMBER = #{cust_number}
